import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { postThunk } from "../redux/PostSlice";
import { useNavigate } from "react-router-dom";

export default function Post() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [itemInfo, setItemInfo] = useState({
    itemName: "",
    itemPrice: "",
    description: "",
    category: "",
  });

  const categoryChoice = [
    "category1",
    "category2",
    "category3",
    "category4",
    "category5",
    "category6",
    "category7",
    "category8",
    "category9",
    "category10",
  ];

  const handleSubmit = (event) => {
    event.preventDefault();
    const { info, value } = event.target;
    setItemInfo((prevValue) => ({
      ...prevValue,
      [info]: value,
    }));
  };

  return (
    <div>
      <form>
        <div>
          <label htmlFor="itemName">Item Name:</label>
          <input type="text" id="itemName" onChange={handleSubmit} />
        </div>
        <div>
          <label htmlFor="itemPrice">Item Price:</label>
          <input
            type="number"
            id="itemPrice"
            step="0.01"
            min="0"
            onChange={handleSubmit}
          />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea id="description" onChange={handleSubmit} />
        </div>
        <div>
          <label htmlFor="category">Category:</label>
          <select id="category" onChange={handleSubmit}>
            <option value="" disabled>
              Choose a category
            </option>
            {categoryChoice.map((choice) => (
              <option key={choice} value={choice}>
                {choice}
              </option>
            ))}
          </select>
        </div>
        <button
          type="submit"
          onClick={() => {
            dispatch(postThunk(itemInfo));
          }}>
          Submit
        </button>
      </form>
    </div>
  );
}
