import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = {
  isFilled: false,
};

export const postSlice = createSlice({
  name: "post",
  initialState,
  reducers: {
    isFilled: (state) => {
      state.isFilled = true;
    },
    notFilled: (state) => {
      state.isFilled = false;
    },
  },
});

export const postThunk =
  ({ itemName, itemPrice, description, category, photo1, photo2, photo3,userName }) =>
  async (dispatch) => {
    const formData = new FormData();
    formData.append("itemName", itemName);
    formData.append("itemPrice", itemPrice);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("photo1", photo1);
    formData.append("photo2", photo2);
    formData.append("photo3", photo3);

    if (photo1) {
      formData.append("photo1Name", photo1.name);
    } else {
      formData.append("photo1Name", "null");
    }

    if (photo2) {
      formData.append("photo2Name", photo2.name);
    } else {
      formData.append("photo2Name", "null");
    }

    if (photo3) {
      formData.append("photo3Name", photo3.name);
    } else {
      formData.append("photo3Name", "null");
    }

    await axios.post(`http://localhost:8000/post`, formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
    dispatch(isFilled());
  };

export const { isFilled, notFilled } = postSlice.actions;

export default postSlice.reducer;
