import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = {
  isFilled: false,
};

export const postSlice = createSlice({
  name: "post",
  initialState,
  reducers: {
    isFilled: (state) => {
      state.isFilled = true;
    },
    notFilled: (state) => {
      state.isFilled = false;
    },
  },
});


export const postThunk =
  ({ itemName, itemPrice, description, category }) =>
  async () => {
    console.log({ itemName, itemPrice, description, category });
    let response = await axios.post(`http://localhost:8000/post`, {
      itemName,
      itemPrice,
      description,
      category,
    });
    console.log(response.data);
    dispatch(isFilled);
  };

export const { isFilled, notFilled } = authSlice.actions;

export default postSlice.reducer;
