import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { postThunk } from "../redux/PostSlice";
import Navbar from "./Navbar";
import Search from "./Search";
import "./post.css";

import "bootstrap/dist/css/bootstrap.min.css";

export default function Test() {
  const [selectedFile, setSelectedFile] = useState(null);

  const handlePhotoSubmit = (event) => {
    setSelectedFile(URL.createObjectURL(event.target.files[0]));
  };

  const handleSubmit = "";
  const itemInfo = "";
  const categoryChoice= "";


  return (
    <div>
      <Navbar />
      <Search />
      <div className="container postContainer">
        <div className="row">
          <div
            className="col-lg-6 col-md-12 postPhoto"
            style={{ backgroundImage: `url(${selectedFile})` }}>
            <div>
              <label htmlFor="photo1">
                <input
                  type="file"
                  id="photo1"
                  accept="image/*"
                  onChange={handlePhotoSubmit}
                  style={{ display: "none" }}
                />
                <div className="custom-file-input">
                  <i class="fa-solid fa-cloud-arrow-up uploadIcon"></i>
                  {/* <div>Upload your item hoto...</div> */}
                </div>
              </label>
            </div>
          </div>
          <div className="col-lg-6 col-md-12">
            <div>
              <label htmlFor="itemName">Item Name:</label>
              <input type="text" id="itemName" onChange={handleSubmit} />
            </div>
            <div>
              <label htmlFor="itemPrice">Item Price:</label>
              <input
                type="number"
                id="itemPrice"
                step="0.01"
                min="0"
                onChange={handleSubmit}
              />
            </div>
            <div>
              <label htmlFor="description">Description:</label>
              <textarea id="description" onChange={handleSubmit} />
            </div>
            <div>
              <label htmlFor="category">Category:</label>
              <select
                id="category"
                defaultValue={itemInfo.category}
                onChange={handleSubmit}>
                <option value="null" disabled>
                  Choose a category
                </option>
                {categoryChoice.map((choice) => (
                  <option key={choice} value={choice}>
                    {choice}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
