import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import Navbar from "../Components/Navbar";
import Search from "../Components/Search";
import Footer from "../Components/Footer";
import axios from "axios";
import moment from "moment";

import "./message.css";

export default function Message() {
  const [messageLog, setMessageLog] = useState([]);
  useEffect(() => {
    axios
      .get(
        `http://localhost:8000/messagelog/${localStorage.getItem("username")}`
      )
      .then((res) => {
        if (res.data !== "empty") {
          setMessageLog(res.data[0]);
          console.log(res.data[0]);
        }
      })
      .catch((err) => {
        console.error(err);
      });
  }, [messageLog]);

  return (
    <div className="messagePage">
      <Navbar />
      <Search />

      <div className="container allMessageContainer">
        <div className="row messageRowContainer">
          <div className="col-2">
            <div className="targetNameContainer">
              <div className="targetNameContainerText">{messageLog.sender}</div>
            </div>
          </div>
          <div className="col-10">
            <div className="row">
              <div className="col-12">
                <div className="recentMessageContainer">
                  <div className="recentMessage">{messageLog.message}</div>
                </div>
              </div>
              <div className="col-12">
                <div className="row">
                  <div className="col-10">
                    <textarea className="messageArea" />
                  </div>
                  <div className="col-2 sendMessageContainer">
                    <button className="">Send!</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}
