import React, { useState } from "react";
import "./banner.css";

export default function Banner() {
  const [selectedBanner, setSelectedBanner] = useState(0);
  const banners = [
    { id: 0, image: "banner1.jpeg" },
    { id: 1, image: "banner2.jpeg" },
    { id: 2, image: "banner3.jpeg" },
    { id: 3, image: "banner4.jpeg" },
  ];

  const handleLeftClick = () => {
    if (selectedBanner === 0) {
      setSelectedBanner(banners.length - 1);
    } else {
      setSelectedBanner(selectedBanner - 1);
    }
  };

  const handleRightClick = () => {
    if (selectedBanner === banners.length - 1) {
      setSelectedBanner(0);
    } else {
      setSelectedBanner(selectedBanner + 1);
    }
  };

  return (
    <div className="container">
      <div className="banner-container">
        <button className="left-button" onClick={handleLeftClick}>
          {"<"}
        </button>
        <div className="banner-images">
          <img
            className="banner-image"
            src={`/${banners[selectedBanner].image}`}
            alt={`Banner ${selectedBanner + 1}`}
          />
          <img
            className="banner-image"
            src={`${banners[(selectedBanner + 1) % banners.length].image}`}
            alt={`Banner ${(selectedBanner + 2) % (banners.length + 1)}`}
          />
        </div>
        <button className="right-button" onClick={handleRightClick}>
          {">"}
        </button>
      </div>
    </div>
  );
}
