import React, { useState } from "react";
import "./banner.css";

export default function Banner() {
  const banners = [
    { id: 0, image: "banner1.jpeg" },
    { id: 1, image: "banner2.jpeg" },
    { id: 2, image: "banner3.jpeg" },
    { id: 3, image: "banner4.jpeg" },
  ];
  const [selectedBanner, setSelectedBanner] = useState(Banners);

  const handleLeftClick = () => {
    if (selectedBanner === 0) {
      setSelectedBanner(banners.length - 1);
    } else {
      setSelectedBanner(selectedBanner - 1);
    }
  };

  const handleRightClick = () => {
    if (selectedBanner === banners.length - 1) {
      setSelectedBanner(0);
    } else {
      setSelectedBanner(selectedBanner + 1);
    }
  };

  return (
    <div className="bannerContainer">
      <button id="left-btn">left</button>
      <div className="photoContainer">
        <div id="bannerDisplay1Div">
          <img id="bannerDisplay1" src="./banner1.jpeg" alt="" />
        </div>

        <div id="bannerDisplay2Div">
          <img id="bannerDisplay2" src="./banner2.jpeg" alt="" />
        </div>
      </div>
      <button id="right-btn">right</button>
    </div>
  );
}
