import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = {
  isFilled: false,
};

export const postSlice = createSlice({
  name: "post",
  initialState,
  reducers: {
    isFilled: (state) => {
      state.isFilled = true;
    },
    notFilled: (state) => {
      state.isFilled = false;
    },
  },
});

export const postThunk = (itemInfo) => async (dispatch) => {
  console.log(itemInfo);
  let response = await axios.post(`http://localhost:8000/post`, itemInfo);
  console.log(response.data);
  dispatch(isFilled());
};

