import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import jwt from "jwt-decode";

const initialState = {
  isAuthenticated: localStorage.getItem("token") != null || false,
};

export const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    signin: (state) => {
      state.isAuthenticated = true;
    },
    signout: (state) => {
      state.isAuthenticated = false;
    },
    userReducer: (state = initialState, action) => {
      switch (action.type) {
        case "SET_USER":
          return {
            ...state,
            username: action.payload.username,
            id: action.payload.id,
          };
        default:
          return state;
      }
    },
  },
});

export const setUser = (username, id) => ({
  type: "SET_USER",
  payload: {
    username,
    id,
  },
});

export const signupThunk =
  ({ username, password }) =>
  async () => {
    console.log({ username, password });
    let response = await axios.post(`http://localhost:8000/signup`, {
      username,
      password,
    });
    console.log(response.data);
  };

export const signinThunk =
  ({ username, password }) =>
  async (dispatch) => {
    console.log({ username, password });
    let response = await axios.post(`http://localhost:8000/signin`, {
      username,
      password,
    });
    localStorage.setItem("token", response.data);
    dispatch(signin());

    const decodedToken = jwt.verify(response.data, process.env.JWT_SECRET);
    dispatch(setUser(username, decodedToken.id));
  };

export const signoutThunk = () => async (dispatch) => {
  localStorage.removeItem("token");
  dispatch(signout);
};

// Action creators are generated for each case reducer fu nction
export const { signin, signout } = authSlice.actions;

export default authSlice.reducer;
