import React, { useState } from "react";
import "./banner.css";

export default function Banner() {
  const banners = [
    { id: 0, image: "banner1.jpeg" },
    { id: 1, image: "banner2.jpeg" },
    { id: 2, image: "banner3.jpeg" },
    { id: 3, image: "banner4.jpeg" },
  ];

  const [selectedBanner, setSelectedBanner] = useState(0);

  const handleLeftClick = () => {
    if (selectedBanner === 0) {
      setSelectedBanner(banners.length - 1);
    } else {
      setSelectedBanner(selectedBanner - 1);
    }
  };

  const handleRightClick = () => {
    if (selectedBanner === banners.length - 1) {
      setSelectedBanner(0);
    } else {
      setSelectedBanner(selectedBanner + 1);
    }
  };

  return (
    <div class="container jumbotron-container">
      <div class="row">
        <div class="col-12">
          <h1>
            <span>
              <img src="/" alt="" width="25px" />
            </span>
            Linked List
            <span>
              <img src="./assets/linked.png" alt="" width="25px" />
            </span>
          </h1>
          <h3>
            Unleash your dreams
            <span id="rocket-animation">
              <i class="bi bi-rocket-takeoff"></i>
            </span>
          </h3>
        </div>
      </div>
    </div>
  );
}
