import React, { useState } from "react";

export default function Test() {
  const [selectedFile, setSelectedFile] = useState(null);

  const handlePhotoSubmit = (event) => {
    setSelectedFile(URL.createObjectURL(event.target.files[0]));
  };

  return (
    <div>
      ...
      <div className="container postContainer">
        <div className="row">
          <div
            className="col-lg-6 col-md-12 postPhoto"
            style={{ backgroundImage: `url(${selectedFile})` }}
          >
            <div>
              <label htmlFor="photo1">
                <input
                  type="file"
                  id="photo1"
                  accept="image/*"
                  onChange={handlePhotoSubmit}
                  style={{ display: "none" }}
                />
                <div className="custom-file-input">
                  <i className="fa-solid fa-cloud-arrow-up uploadIcon"></i>
                  <div>Upload your item photo...</div>
                </div>
              </label>
            </div>
          </div>
          <div className="col-lg-6 col-md-12">testing</div>
        </div>
      </div>
    </div>
  );
}
